import React, { useState } from 'react';

function App() {
    const [file, setFile] = useState(null);
    const [docId, setDocId] = useState('');
    const [question, setQuestion] = useState('');
    const [answer, setAnswer] = useState('');
    const [summary, setSummary] = useState('');
    const [pdfUrl, setPdfUrl] = useState('');

    const handleFileChange = (event) => {
        setFile(event.target.files[0]);
    };

    const handleUpload = async () => {
        if (!file) {
            alert('Please select a file first.');
            return;
        }

        const formData = new FormData();
        formData.append('file', file);

        try {
            const response = await fetch('http://localhost:5000/upload', {
                method: 'POST',
                body: formData,
            });

            if (!response.ok) {
                console.error('Response status:', response.status);
                console.error('Response text:', await response.text());
                throw new Error('Upload failed');
            }

            const result = await response.json();
            setDocId(result.doc_id);
            alert('Document uploaded successfully! Document ID: ' + result.doc_id);
        } catch (error) {
            console.error('Error uploading document:', error);
            alert('Error uploading document. Please try again.');
        }
    };

    const handleQuestionSubmit = async () => {
        if (!docId || !question) {
            alert('Please upload a document and enter a question.');
            return;
        }

        const response = await fetch('http://localhost:5000/ask', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                doc_id: docId,
                question: question,
            }),
        });

        if (!response.ok) {
            throw new Error('Failed to get an answer');
        }

        const result = await response.json();
        setAnswer(result.answer);
    };

    const handleSummarize = async () => {
        if (!docId) {
            alert('Please upload a document first.');
            return;
        }

        const response = await fetch('http://localhost:5000/summarize', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ doc_id: docId }),
        });

        if (!response.ok) {
            throw new Error('Failed to summarize document');
        }

        const result = await response.json();
        setSummary(result.summary);
    };

    const handleDownloadPdf = async () => {
        if (!docId) {
            alert('Please upload a document first.');
            return;
        }

        const response = await fetch('http://localhost:5000/download_pdf', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ doc_id: docId }),
        });

        if (!response.ok) {
            throw new Error('Failed to generate PDF');
        }

        const result = await response.json();
        setPdfUrl(result.pdf_url);
    };

    return (
        <div className="App">
            <h1>Document QA App</h1>
            <div>
                <input type="file" onChange={handleFileChange} />
                <button onClick={handleUpload}>Upload Document</button>
            </div>
            <div>
                <input
                    type="text"
                    value={question}
                    onChange={(e) => setQuestion(e.target.value)}
                    placeholder="Ask a question"
                />
                <button onClick={handleQuestionSubmit}>Submit Question</button>
            </div>
            {answer && (
                <div>
                    <h2>Answer:</h2>
                    <p>{answer}</p>
                </div>
            )}
            <div>
                <button onClick={handleSummarize}>Summarize Document</button>
            </div>
            {summary && (
                <div>
                    <h2>Summary:</h2>
                    <p>{summary}</p>
                </div>
            )}
            <div>
                <button onClick={handleDownloadPdf}>Download PDF</button>
                {pdfUrl && (
                    <div>
                        <a href={`http://localhost:5000/${pdfUrl}`} download>
                            Download PDF
                        </a>
                    </div>
                )}
            </div>
        </div>
    );
}

export default App;
